import java.util.Random;
import java.util.Scanner;

public class Minesweeper {
    private static final char MINE = '*';
    private static final char EMPTY = '.';
    private static final char REVEALED_EMPTY = ' ';
    private static final char FLAG = 'F';

    private int gridSize;
    private int numMines;
    private char[][] minefield;
    private char[][] revealedCells;

    public Minesweeper(int gridSize, int numMines) {
        this.gridSize = gridSize;
        this.numMines = numMines;
        generateMinefield();
        revealedCells = new char[gridSize][gridSize];
        for (int i = 0; i < gridSize; i++) {
            for (int j = 0; j < gridSize; j++) {
                revealedCells[i][j] = EMPTY;
            }
        }
    }

    private void generateMinefield() {
        minefield = new char[gridSize][gridSize];
        for (int i = 0; i < gridSize; i++) {
            for (int j = 0; j < gridSize; j++) {
                minefield[i][j] = EMPTY;
            }
        }

        Random random = new Random();
        for (int i = 0; i < numMines; i++) {
            int x, y;
            do {
                x = random.nextInt(gridSize);
                y = random.nextInt(gridSize);
            } while (minefield[x][y] == MINE);

            minefield[x][y] = MINE;
        }
    }

    public boolean revealCell(int x, int y) {
        if (minefield[x][y] == MINE) {
            return false;
        }

        revealSafeCells(x, y);
        return true;
    }

    private void revealSafeCells(int x, int y) {
        if (x < 0 || x >= gridSize || y < 0 || y >= gridSize) {
            return;
        }

        if (revealedCells[x][y] != EMPTY) {
            return;
        }

        revealedCells[x][y] = REVEALED_EMPTY;

        if (minefield[x][y] == EMPTY) {
            for (int i = -1; i <= 1; i++) {
                for (int j = -1; j <= 1; j++) {
                    revealSafeCells(x + i, y + j);
                }
            }
        } else {
            revealedCells[x][y] = minefield[x][y];
        }
    }

    public void flagCell(int x, int y) {
        if (revealedCells[x][y] == EMPTY) {
            revealedCells[x][y] = FLAG;
        } else if (revealedCells[x][y] == FLAG) {
            revealedCells[x][y] = EMPTY;
        }
    }

    public boolean hasWon() {
        for (int i = 0; i < gridSize; i++) {
            for (int j = 0; j < gridSize; j++) {
                if (minefield[i][j] != MINE && revealedCells[i][j] == EMPTY) {
                    return false;
                }
            }
        }
        return true;
    }

    public void printRevealedCells() {
        for (int i = 0; i < gridSize; i++) {
            for (int j = 0; j < gridSize; j++) {
                System.out.print(revealedCells[i][j]);
            }
            System.out.println();
        }
    }

    public static void main(String[] args) {
        if (args.length != 2) {
            System.out.println("Usage: java Minesweeper <gridSize> <numMines>");
            return;
        }

        int gridSize = Integer.parseInt(args[0]);
        int numMines = Integer.parseInt(args[1]);

        Minesweeper game = new Minesweeper(gridSize, numMines);
        Scanner scanner = new Scanner(System.in);

        while (true) {
            game.printRevealedCells();
            System.out.println("Enter command (R/F x y):");
            String command = scanner.next();
            int x = scanner.nextInt();
            int y = scanner.nextInt();

            if (command.equalsIgnoreCase("R")) {
                if (!game.revealCell(x, y)) {
                    System.out.println("You hit a mine! Game over.");
                    break;
                }
            } else if (command.equalsIgnoreCase("F")) {
                game.flagCell(x, y);
            } else {
                System.out.println("Invalid command. Use R for reveal or F for flag.");
                continue;
            }

            if (game.hasWon()) {
                System.out.println("Congratulations! You won!");
                break;
            }
        }

        scanner.close();
    }
}

